package br.com.hackccr.features.courses

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import br.com.hackccr.R
import br.com.hackccr.data.Course
import com.bumptech.glide.Glide
import kotlinx.android.synthetic.main.course_highlight_item.*
import kotlinx.android.synthetic.main.fragment_news.*

class CoursesFragment : Fragment() {

    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {
        return inflater.inflate(R.layout.fragment_news, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        configureHighlight()
        generateCourse()
    }

    private fun configureHighlight() {
        Glide.with(this)
            .load("https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSzngNOV4fHJIPF5b4iU5-P9JZ-p-e4fwyIu3C21biY1z6eqqVp&usqp=CAU")
            .into(ivThumbnail)
    }

    private fun generateCourse() {
        val courses = mutableListOf<Course>()
        courses.add(Course(
            "data:image/jpeg;base64,",
            "Direção defensiva: como evitar acidentes na estrada")
        )

        configureCoursesMoreAccess(courses)
    }

    private fun configureCoursesMoreAccess(courses: List<Course>) {
        val adapter = CourseAdapter(courses)

        recyclerView.isNestedScrollingEnabled = false
        recyclerView.layoutManager = LinearLayoutManager(this.requireActivity(), RecyclerView.VERTICAL, false)
        recyclerView.adapter = adapter
    }

    companion object {
        fun newInstance() = CoursesFragment()
    }
}